     1const PDFDocument = require('pdfkit');
     2const fs = require('fs');
     3const path = require('path');
     4const GeminiService = require('./geminiService');
     5const { ChartJSNodeCanvas } = require('chartjs-node-canvas');
     6
     7class GeminiPdfGenerator {
     8  constructor(patientData, algorithmResults, qeegData) {
     9    this.patientData = patientData;
    10    this.algorithmResults = algorithmResults;
    11    this.qeegData = qeegData;
    12    this.geminiReportData = null;
    13  }
    14
    15  /**
    16   * Transform algorithm results to Gemini input format
    17   */
    18  transformToGeminiFormat() {
    19    const brainParameters = {
    20      Cognition: null,
    21      Stress: null,
    22      FocusAndAttention: null,
    23      BurnoutAndFatigue: null,
    24      EmotionalRegulation: null,
    25      Learning: null,
    26      Creativity: null
    27    };
    28
    29    // Map the parameters from algorithm results
    30    this.algorithmResults.parameters.forEach(param => {
    31      const paramName = param.name.replace(/\s+/g, '').replace(/&/g, 'And');
    32      if (brainParameters.hasOwnProperty(paramName)) {
    33        brainParameters[paramName] = {
    34          score: param.score,
    35          maxScore: param.maxScore,
    36          bucket: param.bucket || param.classification,
    37          subparameters: (param.metrics || param.subParameters || []).map(metric => ({
    38            name: metric.name,
    39            score: metric.score,
    40            value: metric.value,
    41            threshold: metric.threshold
    42          }))
    43        };
    44      }
    45    });
    46
    47    return brainParameters;
    48  }
    49
    50  /**
    51   * Generate report structure using Gemini AI
    52   */
    53  async generateReportStructure() {
    54    console.log('\n === Generating report structure with Gemini AI ===');
    55    const brainParameters = this.transformToGeminiFormat();
    56
    57    console.log(' INPUT DATA TO GEMINI (Brain Parameters):');
    58    Object.keys(brainParameters).forEach(paramKey => {
    59      const param = brainParameters[paramKey];
    60      if (param) {
    61        console.log(`   ${paramKey}: score=${param.score}/${param.maxScore}, bucket=${param.bucket}, subParams=${param.subparameters?.length || 0}`);
    62      }
    63    });
    64
    65    try {
    66      const result = await GeminiService.generateBrainPerformanceReport(brainParameters);
    67
    68      if (!result.success) {
    69        console.error(' Gemini API returned error:', result.error);
    70        throw new Error(`Gemini AI failed: ${result.error}`);
    71      }
    72
    73      this.geminiReportData = result.data;
    74      console.log('\n Gemini report structure generated successfully');
    75      console.log(' OUTPUT FROM GEMINI:');
    76      if (result.data.parameters) {
    77        result.data.parameters.forEach(param => {
    78          console.log(`   ${param.name}: score=${param.score}/${param.maxScore}, bucket=${param.bucket}`);
    79        });
    80      }
    81
    82      return this.geminiReportData;
    83    } catch (error) {
    84      console.error(' Error in generateReportStructure:', error.message);
    85      console.error('   Stack:', error.stack);
    86      throw error;
    87    }
    88  }
    89
    90  /**
    91   * Main report generation function
    92   */
    93  async generateReport(outputPath) {
    94    try {
    95      // Step 1: Try to generate AI report structure with Gemini
    96      let usingGemini = false;
    97      try {
    98        console.log(' Attempting to generate report with Gemini AI...');
    99        await this.generateReportStructure();
   100        usingGemini = true;
   101        console.log(' Gemini AI report structure ready');
   102      } catch (geminiError) {
   103        console.error(' Gemini AI failed, using fallback data structure');
   104        console.error('   Gemini Error:', geminiError.message);
   105
   106        // Fallback: Create basic report structure from algorithm data
   107        this.geminiReportData = this.createFallbackReportStructure();
   108        console.log(' Fallback report structure created');
   109      }
   110
   111      // Step 2: Create PDF from the report structure (AI or fallback)
   112      const doc = new PDFDocument({
   113        size: 'A4',
   114        margins: { top: 50, bottom: 50, left: 50, right: 50 }
   115      });
   116
   117      const stream = fs.createWriteStream(outputPath);
   118      doc.pipe(stream);
   119
   120      // Generate PDF content
   121      await this.generatePDFContent(doc);
   122
   123      doc.end();
   124
   125      // Wait for PDF to finish writing
   126      await new Promise((resolve, reject) => {
   127        stream.on('finish', resolve);
   128        stream.on('error', reject);
   129      });
   130
   131      const sourceType = usingGemini ? 'Gemini AI' : 'Fallback (Basic)';
   132      console.log(` PDF generated successfully with ${sourceType} content`);
   133      return outputPath;
   134
   135    } catch (error) {
   136      console.error(' Error generating PDF:', error);
   137      console.error('   Stack:', error.stack);
   138      throw error;
   139    }
   140  }
   141
   142  /**
   143   * Create fallback report structure when Gemini fails
   144   */
   145  createFallbackReportStructure() {
   146    console.log(' Creating fallback report structure...');
   147    console.log('   Algorithm results:', JSON.stringify(this.algorithmResults, null, 2));
   148
   149    const parameters = this.algorithmResults.parameters.map(param => {
   150      // Use metrics (correct field from algorithm) instead of subParameters
   151      const subMetrics = param.metrics || param.subParameters || [];
   152
   153      console.log(`   Processing ${param.name}:`);
   154      console.log(`     Score: ${param.score}/${param.maxScore}`);
   155      console.log(`     Classification: ${param.classification}`);
   156      console.log(`     Metrics count: ${subMetrics.length}`);
   157
   158      return {
   159        name: param.name,
   160        score: param.score,
   161        bucket: param.classification || 'Average',
   162        bucketColor: this.getBucketColor(param.classification, param.name),
   163        summary: `${param.name} score is ${param.score} out of ${param.maxScore} (${param.classification || 'Average'})`,
   164        subparameters: subMetrics.map(metric => ({
   165          name: metric.name || 'Metric',
   166          score: metric.score !== undefined ? metric.score : (typeof metric.value === 'number' ? metric.value : 0),
   167          interpretation: metric.score === 1 ? ' Within optimal range' : ' Outside optimal range',
   168          details: metric.description || (metric.value !== undefined && typeof metric.value === 'number' ? `Value: ${metric.value.toFixed(2)}` : '')
   169        }))
   170      };
   171    });
   172
   173    console.log('   Fallback structure created with', parameters.length, 'parameters');
   174
   175    return {
   176      title: 'Brain Performance Report',
   177      patientSummary: `This report shows the analysis of ${this.algorithmResults.parameters.length} brain performance parameters for ${this.patientData.name}. Overall score: ${this.algorithmResults.overallScore || 'N/A'}.`,
   178      parameters: parameters,
   179      recommendations: [
   180        'Continue monitoring brain health metrics regularly',
   181        'Maintain healthy lifestyle habits including proper sleep and exercise',
   182        'Consult with healthcare provider for detailed interpretation and personalized recommendations'
   183      ]
   184    };
   185  }
   186
   187  /**
   188   * Generate PDF content from Gemini AI data
   189   */
   190  async generatePDFContent(doc) {
   191    const reportData = this.geminiReportData;
   192
   193    console.log('\n === Generating PDF Content ===');
   194    console.log('   Report Title:', reportData.title);
   195    console.log('   Parameters count:', reportData.parameters?.length || 0);
   196
   197    if (reportData.parameters && reportData.parameters.length > 0) {
   198      reportData.parameters.forEach((param, idx) => {
   199        console.log(`   ${idx + 1}. ${param.name}: ${param.score} (${param.bucket}), Sub-metrics: ${param.subparameters?.length || 0}`);
   200      });
   201    }
   202
   203    // Header
   204    this.addHeader(doc);
   205
   206    // Add watermark to first page
   207    this.addWatermark(doc);
   208
   209    // Patient Information
   210    this.addPatientInfo(doc);
   211
   212    doc.moveDown(0.3);
   213
   214    // Title with decorative elements - Infographic Style
   215    const titleY = doc.y;
   216
   217    // Decorative line before title
   218    doc.moveTo(150, titleY)
   219       .lineTo(445, titleY)
   220       .lineWidth(2)
   221       .stroke('#4299E1');
   222
   223    doc.moveDown(0.3);
   224
   225    doc.fontSize(20)
   226       .fillColor('#323956')
   227       .font('Helvetica-Bold')
   228       .text(reportData.title || 'Brain Performance Infographic', 50, doc.y, { width: 495, align: 'center' });
   229
   230    // Subtitle if available
   231    if (reportData.subtitle) {
   232      doc.fontSize(11)
   233         .fillColor('#4A5568')
   234         .font('Helvetica')
   235         .text(reportData.subtitle, 50, doc.y, { width: 495, align: 'center' });
   236    }
   237
   238    doc.moveDown(0.3);
   239
   240    // Decorative line after title
   241    const afterTitleY = doc.y;
   242    doc.moveTo(150, afterTitleY)
   243       .lineTo(445, afterTitleY)
   244       .lineWidth(2)
   245       .stroke('#4299E1');
   246
   247    doc.moveDown(0.5);
   248
   249    // === RADAR CHART - Visual Infographic ===
   250    try {
   251      const chartBuffer = await this.generateRadarChart();
   252      if (chartBuffer) {
   253        // Add chart to PDF
   254        const chartX = (595 - 400) / 2; // Center on A4 page (595px wide)
   255        const chartY = doc.y;
   256
   257        doc.image(chartBuffer, chartX, chartY, {
   258          width: 400,
   259          height: 400,
   260          align: 'center'
   261        });
   262
   263        console.log(` Radar chart embedded in PDF at position (${chartX}, ${chartY})`);
   264
   265        // Move cursor below chart - reduced spacing
   266        doc.y = chartY + 380;
   267      }
   268    } catch (chartError) {
   269      console.error('  Could not add radar chart to PDF:', chartError.message);
   270      // Continue without chart if it fails
   271    }
   272
   273    // Parameters Section
   274    if (reportData.parameters && reportData.parameters.length > 0) {
   275      console.log('\n Adding parameters to PDF...');
   276      reportData.parameters.forEach((param, index) => {
   277        this.addParameterSection(doc, param, index);
   278      });
   279      console.log(' All parameters added to PDF\n');
   280    } else {
   281      console.error(' No parameters found in report data!');
   282    }
   283
   284    doc.moveDown(1);
   285
   286    // === NEW: Extracted QEEG Data Section ===
   287    console.log(' Adding Extracted QEEG Data section...');
   288    this.addExtractedDataSection(doc);
   289    console.log(' Extracted data section added\n');
   290
   291    doc.moveDown(1);
   292
   293    // === NEW: Detailed Calculations Section ===
   294    console.log(' Adding Detailed Calculations section...');
   295    this.addCalculationsSection(doc);
   296    console.log(' Calculations section added\n');
   297
   298    doc.moveDown(1);
   299
   300    // Patient Summary
   301    if (reportData.patientSummary) {
   302      this.addSummarySection(doc, reportData.patientSummary);
   303    }
   304
   305    // Recommendations
   306    if (reportData.recommendations && reportData.recommendations.length > 0) {
   307      this.addRecommendations(doc, reportData.recommendations);
   308    }
   309
   310    // Footer
   311    this.addFooter(doc);
   312  }
   313
   314  /**
   315   * Add header to PDF - Infographic Style
   316   */
   317  addHeader(doc) {
   318    // Bold colored header bar for infographic look
   319    doc.rect(0, 0, 595, 60)
   320       .fill('#323956');
   321
   322    // Add logo to top right if available
   323    try {
   324      // Try multiple possible logo locations
   325      const logoPaths = [
   326        path.join(__dirname, '../../public/header logo.png'),
   327        path.join(__dirname, '../assets/header-logo.png')
   328      ];
   329
   330      let logoPath = null;
   331      for (const testPath of logoPaths) {
   332        if (fs.existsSync(testPath)) {
   333          logoPath = testPath;
   334          break;
   335        }
   336      }
   337
   338      if (logoPath) {
   339        doc.image(logoPath, 470, 5, {
   340          width: 55,
   341          height: 55,
   342          fit: [55, 55]
   343        });
   344      }
   345    } catch (logoError) {
   346      console.log('Logo not found, continuing without it:', logoError.message);
   347    }
   348
   349    doc.fontSize(16)
   350       .fillColor('#FFFFFF')
   351       .font('Helvetica-Bold')
   352       .text('NeuroSense Report', 50, 20);
   353
   354    doc.fontSize(10)
   355       .fillColor('#A8D5FF')
   356       .font('Helvetica')
   357       .text('Brain Performance Analysis', 50, 42);
   358
   359    doc.fontSize(9)
   360       .fillColor('#FFFFFF')
   361       .text(new Date().toLocaleDateString('en-US', { year: 'numeric', month: 'long', day: 'numeric' }), 360, 28);
   362
   363    doc.moveDown(2);
   364  }
   365
   366  /**
   367   * Add patient information - Infographic Style
   368   */
   369  addPatientInfo(doc) {
   370    const y = doc.y;
   371
   372    // Colored info box for visual appeal - increased height for more fields
   373    doc.roundedRect(50, y, 495, 85, 5)
   374       .fillAndStroke('#F7FAFC', '#E2E8F0');
   375
   376    doc.fontSize(11)
   377       .fillColor('#323956')
   378       .font('Helvetica-Bold')
   379       .text('Patient Information', 60, y + 10);
   380
   381    // Row 1: Name and Date of Recording (properly aligned)
   382    const col1X = 60;
   383    const col1ValueX = 145;
   384    const col2X = 310;
   385    const col2ValueX = 430;
   386
   387    doc.fontSize(9)
   388       .fillColor('#4A5568')
   389       .font('Helvetica-Bold')
   390       .text('Name:', col1X, y + 28)
   391       .font('Helvetica')
   392       .fillColor('#2D3748')
   393       .text(this.patientData.name || 'N/A', col1ValueX, y + 28);
   394
   395    doc.font('Helvetica-Bold')
   396       .fillColor('#4A5568')
   397       .text('Date of Recording:', col2X, y + 28)
   398       .font('Helvetica')
   399       .fillColor('#2D3748')
   400       .text(this.patientData.dateOfRecording || this.patientData.recordingDate || new Date().toLocaleDateString('en-US'), col2ValueX, y + 28);
   401
   402    // Row 2: Age and Clinic (properly aligned)
   403    doc.font('Helvetica-Bold')
   404       .fillColor('#4A5568')
   405       .text('Age:', col1X, y + 45)
   406       .font('Helvetica')
   407       .fillColor('#2D3748')
   408       .text(`${this.patientData.age || 'N/A'}`, col1ValueX, y + 45);
   409
   410    if (this.patientData.clinicName) {
   411      doc.font('Helvetica-Bold')
   412         .fillColor('#4A5568')
   413         .text('Clinic:', col2X, y + 45)
   414         .font('Helvetica')
   415         .fillColor('#2D3748')
   416         .text(this.patientData.clinicName, col2ValueX, y + 45);
   417    }
   418
   419    // Row 3: Gender and Handedness (properly aligned)
   420    doc.font('Helvetica-Bold')
   421       .fillColor('#4A5568')
   422       .text('Gender:', col1X, y + 62)
   423       .font('Helvetica')
   424       .fillColor('#2D3748')
   425       .text(this.patientData.gender || 'N/A', col1ValueX, y + 62);
   426
   427    doc.font('Helvetica-Bold')
   428       .fillColor('#4A5568')
   429       .text('Handedness:', col2X, y + 62)
   430       .font('Helvetica')
   431       .fillColor('#2D3748')
   432       .text(this.patientData.handedness || 'N/A', col2ValueX, y + 62);
   433
   434    doc.moveDown(2.5);
   435  }
