   437  /**
   438   * Add watermark to current page
   439   */
   440  addWatermark(doc) {
   441    // Save current position
   442    const currentY = doc.y;
   443
   444    // Save the current graphics state
   445    doc.save();
   446
   447    // Add semi-transparent watermark
   448    doc.opacity(0.05)
   449       .fontSize(50)
   450       .fillColor('#000000')
   451       .font('Helvetica-Bold')
   452       .rotate(-45, { origin: [297.5, 421] })
   453       .text('NeuroSense Report', 150, 400, {
   454         width: 500,
   455         align: 'center'
   456       });
   457
   458    // Restore graphics state
   459    doc.restore();
   460
   461    // Restore position (watermark shouldn't move cursor)
   462    doc.y = currentY;
   463  }
