   465  /**
   466   * Add parameter section - Infographic Style
   467   */
   468  addParameterSection(doc, param, index) {
   469    // Only create new page if we're very close to bottom (need at least 150px for parameter header + some content)
   470    if (doc.y > 700) {
   471      doc.addPage();
   472      this.addWatermark(doc);
   473    }
   474
   475    const y = doc.y;
   476
   477    // Determine background color based on score (Low=Red, Medium=Orange, High=Green)
   478    const bgColor = this.getScoreColor(param.score, param.maxScore || 3, param.name);
   479
   480    // Main colored header
   481    doc.roundedRect(50, y, 495, 35, 5)
   482       .fill(bgColor);
   483
   484    // Parameter name (no icon to avoid rendering issues)
   485    doc.fontSize(16)
   486       .fillColor('#FFFFFF')
   487       .font('Helvetica-Bold')
   488       .text(param.name, 60, y + 10);
   489
   490    // Score badge with color-coded background based on score
   491    const scoreText = param.maxScore ? `${param.score}/${param.maxScore}` : `${param.score}`;
   492    const scoreColor = this.getScoreColor(param.score, param.maxScore || 3, param.name);
   493    const scoreBadgeX = 420;
   494
   495    doc.roundedRect(scoreBadgeX, y + 7, 50, 20, 3)
   496       .fillAndStroke(scoreColor, scoreColor);
   497
   498    doc.fontSize(12)
   499       .fillColor('#FFFFFF')
   500       .font('Helvetica-Bold')
   501       .text(scoreText, scoreBadgeX + 10, y + 11);
   502
   503    // Bucket label (aligned to the right)
   504    doc.fontSize(9)
   505       .fillColor('#FFFFFF')
   506       .font('Helvetica-Bold')
   507       .text(param.bucket || '', 475, y + 13, { width: 60, align: 'right' });
   508
   509    doc.moveDown(1.8);
   510
   511    // Parameter summary with light background box
   512    if (param.summary) {
   513      const summaryY = doc.y;
   514      doc.roundedRect(50, summaryY - 3, 495, 25, 3)
   515         .fill('#F8F9FA');
   516
   517      doc.fontSize(10)
   518         .fillColor('#2D3748')
   519         .font('Helvetica')
   520         .text(param.summary, 60, summaryY, {
   521           width: 475,
   522           align: 'left'
   523         });
   524
   525      doc.moveDown(0.7);
   526    }
   527
   528    // Subparameters (metrics) with visual indicators
   529    if (param.subparameters && param.subparameters.length > 0) {
   530      console.log(`    Adding ${param.subparameters.length} sub-metrics for ${param.name}`);
   531
   532      doc.fontSize(9)
   533         .fillColor('#323956')
   534         .font('Helvetica-Bold')
   535         .text('Key Metrics:', 55, doc.y);
   536
   537      doc.moveDown(0.5);
   538
   539      param.subparameters.forEach((sub, idx) => {
   540        console.log(`     ${idx + 1}. ${sub.name}: score=${sub.score}, interpretation="${sub.interpretation}"`);
   541
   542        const metricY = doc.y;
   543
   544        // For Stress and Burnout, LOW scores (0) are GOOD, so invert the logic
   545        const isProblemParameter = param.name === 'Stress' || param.name === 'Burnout & Fatigue' || param.name === 'Burnout and Fatigue';
   546        const isHealthy = isProblemParameter ? (sub.score === 0) : (sub.score === 1);
   547
   548        const checkColor = isHealthy ? '#38A169' : '#E53E3E';
   549        const checkSymbol = isHealthy ? 'Pass' : 'Fail';
   550
   551        // Visual indicator box (properly aligned)
   552        doc.roundedRect(60, metricY, 30, 12, 2)
   553           .fillAndStroke(checkColor, checkColor);
   554
   555        doc.fontSize(7)
   556           .fillColor('#FFFFFF')
   557           .font('Helvetica-Bold')
   558           .text(checkSymbol, 63, metricY + 2);
   559
   560        // Metric name and interpretation (aligned at x=98)
   561        doc.fontSize(9)
   562           .fillColor('#2D3748')
   563           .font('Helvetica-Bold')
   564           .text(sub.name, 98, metricY, { width: 442, continued: true })
   565           .font('Helvetica')
   566           .fillColor('#4A5568')
   567           .text(` - ${sub.interpretation || ''}`, { width: 442 });
   568
   569        // Add details if available
   570        if (sub.details) {
   571          doc.fontSize(7)
   572             .fillColor('#718096')
   573             .text(`    ${sub.details}`, 98, doc.y);
   574        }
   575
   576        doc.moveDown(0.3);
   577      });
   578    } else {
   579      console.log(`    No sub-metrics for ${param.name}`);
   580    }
   581
   582    doc.moveDown(0.8);
   583  }
