   585  /**
   586   * Add summary section - Infographic Style
   587   */
   588  addSummarySection(doc, summary) {
   589    if (doc.y > 700) {
   590      doc.addPage();
   591      this.addWatermark(doc);
   592    }
   593
   594    const y = doc.y;
   595
   596    // Colored banner for summary
   597    doc.roundedRect(50, y, 495, 30, 5)
   598       .fill('#4299E1');
   599
   600    doc.fontSize(14)
   601       .fillColor('#FFFFFF')
   602       .font('Helvetica-Bold')
   603       .text('Overall Brain Health Summary', 60, y + 8);
   604
   605    doc.moveDown(1.3);
   606
   607    // Summary content box
   608    const contentY = doc.y;
   609    doc.roundedRect(50, contentY, 495, 70, 5)
   610       .fillAndStroke('#EBF8FF', '#4299E1');
   611
   612    doc.fontSize(10)
   613       .fillColor('#2D3748')
   614       .font('Helvetica')
   615       .text(summary, 60, contentY + 10, {
   616         width: 475,
   617         align: 'left'
   618       });
   619
   620    doc.moveDown(2);
   621  }
   622
   623  /**
   624   * Add recommendations - Infographic Style
   625   */
   626  addRecommendations(doc, recommendations) {
   627    if (doc.y > 700) {
   628      doc.addPage();
   629      this.addWatermark(doc);
   630    }
   631
   632    const y = doc.y;
   633
   634    // Colored banner for recommendations
   635    doc.roundedRect(50, y, 495, 30, 5)
   636       .fill('#48BB78');
   637
   638    doc.fontSize(14)
   639       .fillColor('#FFFFFF')
   640       .font('Helvetica-Bold')
   641       .text('Personalized Recommendations', 60, y + 8);
   642
   643    doc.moveDown(1.2);
   644
   645    // Recommendations with numbered badges
   646    recommendations.forEach((rec, idx) => {
   647      const recY = doc.y;
   648
   649      // Numbered badge (professional box style)
   650      doc.roundedRect(60, recY, 20, 16, 2)
   651         .fillAndStroke('#48BB78', '#48BB78');
   652
   653      doc.fontSize(10)
   654         .fillColor('#FFFFFF')
