   878  const calculateAge = (dateOfBirth) => {
   879    if (!dateOfBirth) return 'N/A';
   880    const today = new Date();
   881    const birthDate = new Date(dateOfBirth);
   882    let age = today.getFullYear() - birthDate.getFullYear();
   883    const monthDiff = today.getMonth() - birthDate.getMonth();
   884    if (monthDiff < 0 || (monthDiff === 0 && today.getDate() < birthDate.getDate())) {
   885      age--;
   886    }
   887    return age;
   888  };
