let statusCode = 500;
let userMessage = error.message;

switch (error.code) {
  case 'MISSING_API_KEY':
  case 'INVALID_API_KEY':
  case 'INVALID_API_KEY_FORMAT':
  case 'API_CONNECTION_FAILED':
    statusCode = 503; // Service Unavailable
    userMessage = 'System configuration error: OpenAI API key not properly configured. Please contact support.';
    break;

  case 'PDF_EXTRACTION_FAILED':
    statusCode = 422; // Unprocessable Entity
    userMessage = 'Failed to extract QEEG data from PDF. Please ensure the PDF contains valid QEEG tables with absolute and relative power values.';
    break;

  case 'GEMINI_QUOTA_EXCEEDED':
    statusCode = 429; // Too Many Requests
    userMessage = ' API Quota Exceeded\n\n' +
                 'The Gemini API free tier limit (20 requests/day) has been reached.\n\n' +
                 'Options:\n' +
                 '1. Wait a few hours for the quota to reset (resets daily)\n' +
                 '2. Upgrade to a paid plan at https://ai.google.dev/pricing\n' +
                 '3. Contact support for assistance\n\n' +
                 'Tip: The system caches previously processed files, so re-uploading the same files won\'t consume quota.';
    break;

  default:
    userMessage = error.message || 'Failed to process QEEG files';
}

