// Categorize errors for better user feedback
let statusCode = 500;
let userMessage = error.message;

// Extract retry time from error message if available
let retryTime = null;
const retryMatch = error.message?.match(/retry in ([\d.]+)s/i);
if (retryMatch) {
  retryTime = Math.ceil(parseFloat(retryMatch[1]));
}

// Check for Gemini quota error in error message
if (error.message && (error.message.includes('429') || error.message.includes('quota') || error.message.includes('Too Many Requests'))) {
  error.code = 'GEMINI_QUOTA_EXCEEDED';
}

switch (error.code) {
  case 'MISSING_API_KEY':
  case 'INVALID_API_KEY':
  case 'INVALID_API_KEY_FORMAT':
  case 'API_CONNECTION_FAILED':
    statusCode = 503; // Service Unavailable
    userMessage = 'System configuration error: API key not properly configured. Please contact support.';
    break;

  case 'PDF_EXTRACTION_FAILED':
    statusCode = 422; // Unprocessable Entity
    userMessage = 'Failed to extract QEEG data from PDF. Please ensure the PDF contains valid QEEG tables with absolute and relative power values.';
    break;

  case 'GEMINI_QUOTA_EXCEEDED':
    statusCode = 429; // Too Many Requests

    // Build user-friendly message with retry time if available
    let quotaMessage = 'The Gemini API has reached its request limit.\n\n' +
                      'Solutions:\n';

    if (retryTime) {
      if (retryTime < 60) {
        quotaMessage += `1. Wait ${retryTime} seconds and try again (rate limit will reset)\n`;
      } else {
        const minutes = Math.ceil(retryTime / 60);
        quotaMessage += `1. Wait ${minutes} minute${minutes > 1 ? 's' : ''} and try again\n`;
      }
    } else {
      quotaMessage += '1. Wait a few minutes and try again\n';
    }

    quotaMessage += '2. Re-upload the SAME files you processed before (cached, no quota used)\n' +
                   '3. Upgrade API plan: https://ai.google.dev/pricing\n\n' +
                   ' Cache: System remembers previously uploaded files!';

    userMessage = quotaMessage;
    break;

  default:
    // If error message contains Gemini quota keywords, classify it
    if (userMessage && (userMessage.includes('quota') || userMessage.includes('429') || userMessage.includes('Too Many Requests'))) {
      statusCode = 429;

      let quotaMessage = 'The Gemini AI service has reached its request limit.\n\n' +
                        'Solutions:\n';

      if (retryTime) {
        if (retryTime < 60) {
          quotaMessage += ` Wait ${retryTime} seconds and try again\n`;
        } else {
          const minutes = Math.ceil(retryTime / 60);
          quotaMessage += ` Wait ${minutes} minute${minutes > 1 ? 's' : ''} and try again\n`;
        }
      } else {
        quotaMessage += ' Wait a few minutes and try again\n';
      }

      quotaMessage += ' Re-upload the same files (uses cache, no quota used)\n' +
                     ' Contact administrator to upgrade API plan';

      userMessage = quotaMessage;
    } else {
      userMessage = error.message || 'Failed to process QEEG files';
    }
}
