}

/* Smooth transitions for all interactive elements */
button, a, .transition-smooth {
  transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

/* Image loading optimization */
img {
  image-rendering: -webkit-optimize-contrast;
