}

/* Force hide LastPass, 1Password, and other password manager icons */
input[data-lpignore="true"] ~ div[data-lastpass-icon-root],
input[data-lpignore="true"] + div[data-lastpass-icon-root],
div[data-lastpass-icon-root],
div[data-lastpass-overlay],
div[id^="__lpform_"],
div[style*="2147483647"],
[data-lastpass-root],
[id^="lp-pom-root"],
#lp-pom-root,
