    font-family: 'Cabin', 'Inter', sans-serif;
  }

  #root {
    width: 100%;
    min-height: 100vh;
  }
}

/* Custom styles for landing page */
.hero-gradient {
  background: linear-gradient(135deg, #323956 0%, #232D3C 100%);
}

/* Modern Authentication Styles */
.auth-card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1), 0 1px 3px rgba(0, 0, 0, 0.05);
  border-radius: 24px;
  padding: 3rem 2.5rem;
  width: 100%;
  max-width: 480px;
  margin: 0 auto;
  position: relative;
  overflow: hidden;
}

.auth-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 1px;
  background: linear-gradient(90deg, transparent, rgba(245, 208, 93, 0.6), transparent);
}

.field-wrapper {
  margin-bottom: 1.5rem;
  position: relative;
}

.field-label {
  display: block;
  font-size: 0.875rem;
