.floating-elements::before {
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #323956, #232D3C);
  top: -30px;
  right: -30px;
  animation-delay: 0s;
}

.floating-elements::after {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #F5D05D, #d9b84a);
  bottom: -20px;
  left: -20px;
  animation-delay: 3s;
}

.text-lbl-navy {
  color: #323956;
