    scroll-behavior: auto;
  }

  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }
}

/* Force hide LastPass, 1Password, and other password manager icons */
input[data-lpignore="true"] ~ div[data-lastpass-icon-root],
input[data-lpignore="true"] + div[data-lastpass-icon-root],
div[data-lastpass-icon-root],
div[data-lastpass-overlay],
div[id^="__lpform_"],
div[style*="2147483647"],
[data-lastpass-root],
[id^="lp-pom-root"],
#lp-pom-root,
._lpElement,
[data-form-type="other"] ~ div[data-lastpass-icon-root],
[data-form-type="other"] + div[data-lastpass-icon-root] {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  pointer-events: none !important;
  width: 0 !important;
  height: 0 !important;
}

/* Hide 1Password icons */
[data-form-type="other"] ~ div[data-extension="1password"],
com-1password-button {
  display: none !important;
  visibility: hidden !important;
}

/* Prevent any extension from adding icons to our forms */
input[data-lpignore="true"],
input[data-form-type="other"] {
  background-image: none !important;
}

/* Additional LastPass hiding - by background color */
div[style*="background: rgb(230, 0, 23)"],
div[style*="background: rgb(204, 0, 0)"],
div[style*="background-color: rgb(230, 0, 23)"] {
  display: none !important;
}
/* ==================== 3D ANIMATION STYLES ==================== */

/* Hide scrollbar for horizontal scroll */
.scrollbar-hide {
  -ms-overflow-style: none;
  scrollbar-width: none;
}

.scrollbar-hide::-webkit-scrollbar {
  display: none;
}

/* Floating animation for 3D cards */
@keyframes float {
  0%, 100% {
    transform: translateY(0px) translateZ(0px);
  }
  50% {
    transform: translateY(-20px) translateZ(10px);
  }
}

/* Rotate-Y utility classes */
.rotate-y-12 {
  transform: rotateY(12deg);
}

.group:hover .group-hover\:rotate-y-12 {
  transform: rotateY(12deg);
}

/* 3D Transform utilities */
.preserve-3d {
  transform-style: preserve-3d;
}

.perspective-1000 {
  perspective: 1000px;
}

/* Enhanced hover effects for 3D cards */
@layer utilities {
  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .hover-lift {
    transition: transform 0.3s ease, box-shadow 0.3s ease;
  }

  .hover-lift:hover {
    transform: translateY(-10px) scale(1.05);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  }
}

/* Glowing effect for 3D elements */
.glow-effect {
  filter: drop-shadow(0 0 20px rgba(0, 137, 123, 0.5));
}

.group:hover .glow-effect {
  filter: drop-shadow(0 0 30px rgba(0, 137, 123, 0.7));
}

import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { Mail, Lock, Eye, EyeOff, Loader2 } from 'lucide-react';
import { useAuth } from '../../contexts/AuthContext';

const LoginForm = () => {
  const [showPassword, setShowPassword] = useState(false);

  // Safety check for AuthContext
  let login, loading;
  try {
    const auth = useAuth();
    login = auth.login;
    loading = auth.loading;
  } catch (error) {
    console.error('LoginForm: AuthContext not available', error);
    // Return a loading state while AuthContext initializes
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-[#E4EFFF] via-white to-[#CAE0FF]">
        <div className="text-center">
          <Loader2 className="w-8 h-8 animate-spin text-[#323956] mx-auto mb-4" />
          <p className="text-gray-600">Loading...</p>
        </div>
      </div>
    );
  }

  const navigate = useNavigate();
  const location = useLocation();

  // Get user type from navigation state (from landing page)
  const userTypeFromState = location.state?.userType;

  const {
    register,
    handleSubmit,
    formState: { errors },
    setError,
  } = useForm();

  const onSubmit = async (data) => {
    console.log('HOT: LoginForm: Form submitted with data:', data);

    try {
      const result = await login(data, 'email');
      console.log('TARGET: LoginForm: Login result:', result);

      if (result && result.success) {
        console.log('SUCCESS: LoginForm: Login successful, navigating based on user role');

        // Determine redirect path based on user role
        let redirectPath = '/dashboard'; // Default to dashboard
        if (result.user) {
          switch (result.user.role) {
            case 'super_admin':
              redirectPath = '/admin';
              break;
            case 'clinic_admin':
            case 'clinic':
              redirectPath = '/clinic';
              break;
            case 'patient':
              redirectPath = '/patient-dashboard';
              break;
            default:
              redirectPath = '/dashboard'; // Fallback to dashboard
          }
        }

        // Use intended path if available, otherwise use role-based path
        const from = location.state?.from?.pathname || redirectPath;
        navigate(from, { replace: true });
      } else {
        console.log('ERROR: LoginForm: Login failed:', result?.error);
        setError('root', { 
          type: 'manual', 
          message: result?.error || 'Invalid email or password' 
        });
      }
    } catch (error) {
      console.error('ALERT: LoginForm: Unexpected error:', error);
      setError('root', { 
        type: 'manual', 
        message: error?.message || 'Login failed. Please try again.' 
      });
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-[#E4EFFF] via-[#CAE0FF] to-white py-8 px-4 sm:px-6 lg:px-8 relative overflow-hidden">
      {/* Background decorative elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -left-40 w-80 h-80 bg-gradient-to-br from-[#323956]/30 to-[#232D3C]/30 rounded-full blur-3xl animate-float"></div>
        <div className="absolute -bottom-40 -right-40 w-96 h-96 bg-gradient-to-br from-[#F5D05D]/20 to-[#CAE0FF]/20 rounded-full blur-3xl animate-float" style={{ animationDelay: '2s' }}></div>
        <div className="absolute top-1/2 left-1/4 w-32 h-32 bg-gradient-to-br from-[#323956]/20 to-[#CAE0FF]/20 rounded-full blur-2xl animate-float" style={{ animationDelay: '4s' }}></div>
      </div>

      <div className="auth-card animate-fade-in-up floating-elements relative z-10">
        <div className="text-center mb-6 sm:mb-8 animate-slide-up">
          <h2 className="text-3xl sm:text-4xl font-bold bg-gradient-to-r from-gray-900 to-gray-700 bg-clip-text text-transparent mb-2 sm:mb-3">Welcome Back</h2>
          <p className="text-gray-600 font-medium text-sm sm:text-base">
            {userTypeFromState === 'patient' ? 'Patient Login' :
             userTypeFromState === 'clinic' ? 'Clinic Login' :
             'Sign in to your account'}
          </p>
        </div>

        {/* Login Form */}
        <form onSubmit={handleSubmit(onSubmit)} className="space-y-5 sm:space-y-6 animate-slide-in-right" style={{ animationDelay: '0.6s' }}>
          {errors.root && (
            <div className="bg-red-50/80 backdrop-blur-sm border border-red-200 text-red-700 px-4 py-3 rounded-xl text-sm animate-shake shadow-lg">
              {errors.root.message}
            </div>
          )}

          {/* Email Field */}
          <div className="field-wrapper animate-slide-up" style={{ animationDelay: '0.1s' }}>
            <label htmlFor="email" className="field-label">
              Email Address
            </label>
            <div className="relative">
              <Mail className={`input-icon ${errors.email ? 'text-red-400' : ''}`} />
              <input
                id="email"
                type="email"
                className={`auth-input pl-14 ${errors.email ? 'border-red-400 focus:ring-red-400 focus:border-red-400' : ''}`}
                placeholder="Enter your email"
                {...register('email', {
                  required: 'Email is required',
                  pattern: {
                    value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                    message: 'Invalid email address',
                  },
                })}
              />
            </div>
            {errors.email && (
              <p className="error-message">{errors.email.message}</p>
            )}
          </div>

          {/* Password Field */}
          <div className="field-wrapper animate-slide-up" style={{ animationDelay: '0.2s' }}>
            <label htmlFor="password" className="field-label">
              Password
            </label>
            <div className="relative">
              <Lock className={`input-icon ${errors.password ? 'text-red-400' : ''}`} />
              <input
                id="password"
                type={showPassword ? 'text' : 'password'}
                className={`auth-input pl-14 pr-11 ${errors.password ? 'border-red-400 focus:ring-red-400 focus:border-red-400' : ''}`}
                placeholder="Enter your password"
                {...register('password', {
                  required: 'Password is required',
                  minLength: {
                    value: 6,
                    message: 'Password must be at least 6 characters',
                  },
                })}
              />
              <button
                type="button"
                className="absolute right-3 top-3 h-5 w-5 text-gray-400 hover:text-[#323956] transition-colors duration-200 transform hover:scale-110"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? <EyeOff /> : <Eye />}
              </button>
            </div>
            {errors.password && (
              <p className="error-message">{errors.password.message}</p>
            )}
          </div>

          {/* Remember Me & Forgot Password */}
          <div className="flex items-center justify-between animate-slide-up" style={{ animationDelay: '0.3s' }}>
            <div className="flex items-center group">
              <input
                id="remember-me"
                name="remember-me"
                type="checkbox"
                className="h-4 w-4 text-[#323956] focus:ring-[#323956] border-gray-300 rounded transition-transform duration-200 group-hover:scale-110"
              />
              <label htmlFor="remember-me" className="ml-2 block text-sm text-gray-700 font-medium group-hover:text-gray-900 transition-colors duration-200">
                Remember me
              </label>
            </div>
            <Link
              to="/forgot-password"
              className="text-sm text-[#323956] hover:text-[#232D3C] font-semibold transition-all duration-200 hover:underline hover:underline-offset-4"
            >
              Forgot password?
            </Link>
          </div>

          {/* Submit Button */}
          <button
            type="submit"
            disabled={loading}
            className="auth-button animate-bounce-soft" style={{ animationDelay: '0.4s' }}
          >
            {loading ? (
              <>
                <Loader2 className="h-5 w-5 animate-spin" />
                <span className="animate-pulse">Signing in...</span>
              </>
            ) : (
              <span>Sign In</span>
            )}
          </button>
        </form>

        {/* Sign Up Link - Only show for clinic users */}
        {userTypeFromState === 'clinic' && (
          <div className="mt-8 text-center animate-fade-in" style={{ animationDelay: '0.8s' }}>
            <p className="text-sm text-gray-600">
              Don't have an account?{' '}
              <Link
                to="/register"
                state={{
                  userType: 'clinic',
                  fromLogin: true
                }}
                className="font-semibold text-[#323956] hover:text-[#232D3C] transition-all duration-200 hover:underline hover:underline-offset-4"
              >
                Sign up here
              </Link>
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default LoginForm;

