@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  html {
    scroll-behavior: smooth;
    scroll-padding-top: 80px; /* Account for fixed navbar */
  }

  html, body {
    margin: 0;
    padding: 0;
    width: 100%;
    height: 100%;
    overflow-x: hidden;
  }

  body {
    font-family: 'Cabin', 'Inter', sans-serif;
  }

  #root {
    width: 100%;
    min-height: 100vh;
  }
}

/* Custom styles for landing page */
.hero-gradient {
  background: linear-gradient(135deg, #323956 0%, #232D3C 100%);
}

/* Modern Authentication Styles */
.auth-card {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1), 0 1px 3px rgba(0, 0, 0, 0.05);
  border-radius: 24px;
  padding: 3rem 2.5rem;
