   552â†’  /**
   553â†’   * Calculate MD5 hash of PDF file for caching
   554â†’   * @param {Buffer} dataBuffer - PDF file buffer
   555â†’   * @returns {string} MD5 hash
   556â†’   */
   557â†’  static calculatePDFHash(dataBuffer) {
   558â†’    return crypto.createHash('md5').update(dataBuffer).digest('hex');
   559â†’  }
