   561â†’  /**
   562â†’   * Normalize extracted value to consistent decimal places
   563â†’   * Prevents precision-related variations
   564â†’   * @param {number} value - The value to normalize
   565â†’   * @param {number} decimalPlaces - Number of decimal places (default: 2)
   566â†’   * @returns {number|null} Normalized value
   567â†’   */
   568â†’  static normalizeValue(value, decimalPlaces = 2) {
   569â†’    if (value === null || value === undefined || isNaN(value)) {
   570â†’      return null;
   571â†’    }
   572â†’
   573â†’    // Round to consistent decimal places
   574â†’    const multiplier = Math.pow(10, decimalPlaces);
   575â†’    return Math.round(value * multiplier) / multiplier;
   576â†’  }
