      const missingRelative = requiredChannels.filter(ch => !relativeChannels.includes(ch));

      if (missingAbsolute.length > 0) {
        console.warn(`  âš ï¸  Missing absolute power channels: ${missingAbsolute.join(', ')}`);
      }
      if (missingRelative.length > 0) {
        console.warn(`  âš ï¸  Missing relative power channels: ${missingRelative.join(', ')}`);
      }

      // ğŸ” VALIDATE: Check if relative power sums to ~100% for each channel
      console.log(`\n  ğŸ” Validating relative power values (${condition})...`);
      let incompleteChannels = [];

      for (const channel of requiredChannels) {
        if (extractedData.relative?.[channel]) {
          const bands = extractedData.relative[channel];
          const sum = (bands.Delta || 0) + (bands.Theta || 0) + (bands.Alpha || 0) +
                     (bands.Beta || 0) + (bands.HiBeta || 0);

          // Check if sum is close to 100% (allow Â±5% tolerance)
          if (sum < 95 || sum > 105) {
            incompleteChannels.push({
              channel,
              sum: sum.toFixed(1),
              bands: {
                Delta: bands.Delta || 0,
                Theta: bands.Theta || 0,
                Alpha: bands.Alpha || 0,
                Beta: bands.Beta || 0,
                HiBeta: bands.HiBeta || 0
              }
