      // Same PDF will now ALWAYS return the same values
      extractionCache.set(cacheKey, extractedData);
      console.log(`  ğŸ’¾ Extraction result cached (key: ${pdfHash.substring(0, 12)}...)`);
      console.log(`  ğŸ“Š Cache size: ${extractionCache.size} entries`);

      return extractedData;

    } catch (error) {
