/**
 * Main report generation function
 */
async generateReport(outputPath) {
  try {
    // Step 1: Try to generate AI report structure with Gemini
    let usingGemini = false;
    try {
      console.log('ğŸ¤– Attempting to generate report with Gemini AI...');
      await this.generateReportStructure();
      usingGemini = true;
      console.log('âœ… Gemini AI report structure ready');
    } catch (geminiError) {
      console.error('âš ï¸ Gemini AI failed, using fallback data structure');
      console.error('   Gemini Error:', geminiError.message);

      // Fallback: Create basic report structure from algorithm data
      this.geminiReportData = this.createFallbackReportStructure();
      console.log('âœ… Fallback report structure created');
    }

    // Step 2: Create PDF from the report structure (AI or fallback)
    const doc = new PDFDocument({
      size: 'A4',
      margins: { top: 50, bottom: 50, left: 50, right: 50 }
    });

    const stream = fs.createWriteStream(outputPath);
    doc.pipe(stream);

    // Generate PDF content
    await this.generatePDFContent(doc);

    doc.end();

    // Wait for PDF to finish writing
    await new Promise((resolve, reject) => {
      stream.on('finish', resolve);
      stream.on('error', reject);
    });

    const sourceType = usingGemini ? 'Gemini AI' : 'Fallback (Basic)';
    console.log(`âœ… PDF generated successfully with ${sourceType} content`);
    return outputPath;

  } catch (error) {
    console.error('âŒ Error generating PDF:', error);
    console.error('   Stack:', error.stack);
    throw error;
  }

