    }

    doc.moveDown(1);

    // === NEW: Extracted QEEG Data Section ===
    console.log('ğŸ“Š Adding Extracted QEEG Data section...');
    this.addExtractedDataSection(doc);
    console.log('âœ… Extracted data section added\n');

    doc.moveDown(1);

    // === NEW: Detailed Calculations Section ===
    console.log('ğŸ§® Adding Detailed Calculations section...');
    this.addCalculationsSection(doc);
