              font: {
                size: 20,
                weight: 'bold'
              },
              color: '#323956',
              padding: 20
            },
            legend: {
              display: false
            }
          },
          scales: {
            r: {
              angleLines: {
                display: true,
                color: '#E2E8F0'
              },
              grid: {
                color: '#E2E8F0'
              },
              pointLabels: {
                font: {
                  size: 14,
                  weight: 'bold'
