    doc.moveDown(1);

    // Add calculation details for each parameter
    if (this.algorithmResults.parameters) {
      this.algorithmResults.parameters.forEach((param, index) => {
        this.addParameterCalculation(doc, param, index + 1);
      });
    }
  }

  /**
   * Add individual parameter calculation details
   */
  addParameterCalculation(doc, param, number) {
    // Check if we need new page
    if (doc.y > 650) {
      doc.addPage();
      this.addWatermark(doc);
    }

    // Parameter header
    doc.fontSize(12)
       .fillColor('#323956')
       .font('Helvetica-Bold')
       .text(`${number}. ${param.name} - Score: ${param.score}/3 (${param.classification})`, 50, doc.y);

    doc.moveDown(0.5);

    // Metrics/Sub-calculations
    if (param.metrics && param.metrics.length > 0) {
      param.metrics.forEach((metric, idx) => {
        // Check if we need new page before each metric
        if (doc.y > 720) {
          doc.addPage();
          this.addWatermark(doc);
        }

        // Metric box
        const metricY = doc.y;
        doc.roundedRect(55, metricY, 485, 'auto', 3)
           .lineWidth(1)
           .stroke('#CBD5E0');

        doc.fontSize(10)
           .fillColor('#2D3748')
           .font('Helvetica-Bold')
           .text(`   ${String.fromCharCode(97 + idx)}) ${metric.name}: ${metric.score} point`, 60, metricY + 5, { width: 475 });

        doc.moveDown(0.3);

        // Formula/Description
        if (metric.description) {
          doc.fontSize(9)
             .fillColor('#4A5568')
             .font('Helvetica')
             .text(`      ${metric.description}`, 60, doc.y, { width: 475 });
        }

        // Value details
        if (metric.value !== undefined && metric.value !== null) {
          doc.fontSize(9)
             .fillColor('#2D3748')
             .font('Helvetica');

          if (typeof metric.value === 'object') {
            doc.text(`      Raw Values: ${JSON.stringify(metric.value)}`, 60, doc.y, { width: 475 });
          } else {
            doc.text(`      Calculated Value: ${typeof metric.value === 'number' ? metric.value.toFixed(3) : metric.value}`, 60, doc.y, { width: 475 });
          }
        }

        // Interpretation
        if (metric.score === 1) {
          doc.fontSize(9)
             .fillColor('#22543D')
             .font('Helvetica-Bold')
             .text('      âœ“ PASS - Within optimal range', 60, doc.y, { width: 475 });
        } else {
          doc.fontSize(9)
             .fillColor('#742A2A')
             .font('Helvetica-Bold')
             .text('      âœ— FAIL - Outside optimal range', 60, doc.y, { width: 475 });
        }

        doc.moveDown(0.8);
      });
    }

    doc.moveDown(0.5);
  }
}

module.exports = GeminiPdfGenerator;

